mapper.select:
SELECT VACATION.id as vId, name, username, date, vacation_type as type, contents, approval_flag as approvalFlag, rest_time as restTime FROM EMPLOYEE
INNER JOIN VACATION ON EMPLOYEE.username = VACATION.emp_id
WHERE VACATION.approval_flag = '0';

mapper.update:
UPDATE VACATION
SET approval_flag = #{approvalFlag}
WHERE id = #{vId};
